import React, { useState, useEffect } from 'react';
import { Outlet } from 'react-router-dom';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';

// Импорт компонентов
import Header from './components/Header';
import Footer from './components/Footer';

const AppWrapper = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  position: relative;
`;

const MainContent = styled.main`
  flex: 1;
`;

const LoadingContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: var(--main-bg);
  z-index: 9999;
  flex-direction: column;
`;

const LoadingAscii = styled.pre`
  font-family: monospace;
  white-space: pre;
  line-height: 1.2;
  font-size: 14px;
  color: var(--text-primary);
  text-align: center;
`;

const App = () => {
  const [loading, setLoading] = useState(true);
  const [dots, setDots] = useState(0);
  const [frame, setFrame] = useState(0);

  // Анимация точек загрузки
  useEffect(() => {
    let interval;
    if (loading) {
      interval = setInterval(() => {
        setDots(prev => (prev >= 3 ? 0 : prev + 1));
      }, 400);
    }
    return () => clearInterval(interval);
  }, [loading]);

  // Анимация ASCII арта
  useEffect(() => {
    let interval;
    if (loading) {
      interval = setInterval(() => {
        setFrame(prev => (prev >= 3 ? 0 : prev + 1));
      }, 150);
    }
    return () => clearInterval(interval);
  }, [loading]);

  // Имитация загрузки
  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 3000);
    return () => clearTimeout(timer);
  }, []);

  const loadingFrames = [
    `-\\|/-\\|/-`,
    `⠋⠙⠹⠸⠼⠴⠦⠧⠇⠏`,
    `.oO°Oo.`,
    `▁▂▃▄▅▆▇█▇▆▅▄▃▂▁`
  ];

  // ASCII анимация для загрузки
  const loadingAscii = `
  _                    _ _             ${loadingFrames[0][frame % loadingFrames[0].length]} 
 | |    ___   __ _  __| (_)_ __   __ _ 
 | |   / _ \\ / _\` |/ _\` | | '_ \\ / _\` |${'.'.repeat(dots)}
 | |__| (_) | (_| | (_| | | | | | (_| |
 |_____\\___/ \\__,_|\\__,_|_|_| |_|\\__, |
                                |___/ 
`;

  return (
    <AppWrapper>
      <AnimatePresence>
        {loading ? (
          <motion.div
            key="loader"
            initial={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.5 }}
          >
            <LoadingContainer>
              <LoadingAscii>
                {loadingAscii}
              </LoadingAscii>
            </LoadingContainer>
          </motion.div>
        ) : (
          <>
            <Header />
            <MainContent>
              <Outlet />
            </MainContent>
            <Footer />
          </>
        )}
      </AnimatePresence>
    </AppWrapper>
  );
};

export default App;
