import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import styled from 'styled-components';
import { FaGithub, FaTelegram, FaLinkedin } from 'react-icons/fa';

// Правильный способ: создаем motion-компоненты с помощью styled
const MenuContainer = styled.div`
  position: fixed;
  top: 0;
  right: 0;
  z-index: 999;
`;

// Для фона используем motion.div
const MenuBackground = styled(motion.div)`
  position: fixed;
  top: 0;
  right: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgba(10, 25, 47, 0.9);
  backdrop-filter: blur(5px);
`;

// Для кнопки меню
const MenuButton = styled.button`
  position: fixed;
  top: 1.5rem;
  right: 1.5rem;
  width: 50px;
  height: 50px;
  border-radius: 50%;
  background: transparent;
  border: none;
  cursor: pointer;
  z-index: 1001;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 6px;
  padding: 0;
  
  @media (min-width: 769px) {
    display: none;
  }
`;

// Линии для кнопки меню с использованием motion.div
const MenuLine = styled(motion.div)`
  width: 24px;
  height: 2px;
  background-color: var(--accent);
  border-radius: 1px;
`;

// Контент меню с использованием motion.div
const MenuContent = styled(motion.div)`
  position: fixed;
  top: 0;
  right: 0;
  width: 75vw;
  max-width: 400px;
  height: 100vh;
  background-color: var(--bg-secondary);
  box-shadow: -10px 0 30px -15px rgba(2, 12, 27, 0.7);
  padding: 6rem 2rem 2rem;
  z-index: 1000;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

// Список навигации с использованием motion.nav
const NavList = styled(motion.nav)`
  display: flex;
  flex-direction: column;
  gap: 2rem;
  margin-bottom: 3rem;
`;

// Пункт навигации с использованием motion.div
const NavItem = styled(motion.div)`
  text-align: center;
`;

// Ссылка навигации с использованием styled(motion(Link))
const NavLink = styled(motion(Link))`
  color: var(--text-primary);
  text-decoration: none;
  font-size: 1.25rem;
  font-weight: 600;
  letter-spacing: 1px;
  display: inline-block;
  padding: 0.5rem 1rem;
  transition: color 0.3s ease;
  
  &:hover {
    color: var(--accent);
  }
`;

// Контейнер для социальных иконок с использованием motion.div
const SocialContainer = styled(motion.div)`
  display: flex;
  justify-content: center;
  gap: 1.5rem;
  margin-top: auto;
`;

// Социальная ссылка с использованием motion.a
const SocialLink = styled(motion.a)`
  color: var(--text-primary);
  font-size: 1.4rem;
  transition: color 0.3s ease, transform 0.3s ease;
  
  &:hover {
    color: var(--accent);
  }
`;

// Варианты анимации с правильной кривой Безье
const backgroundVariants = {
  closed: { 
    opacity: 0,
    transition: {
      duration: 0.3,
      ease: [0.6, 0.05, 0.01, 0.9],
    }
  },
  open: { 
    opacity: 1,
    transition: {
      duration: 0.3,
      ease: [0.6, 0.05, 0.01, 0.9],
    }
  }
};

const containerVariants = {
  closed: { 
    x: '100%',
    transition: {
      duration: 0.5,
      ease: [0.6, 0.05, 0.01, 0.9],
      staggerChildren: 0.05,
      staggerDirection: -1,
      when: "afterChildren",
    }
  },
  open: { 
    x: 0,
    transition: {
      duration: 0.5,
      ease: [0.6, 0.05, 0.01, 0.9],
      staggerChildren: 0.1,
      delayChildren: 0.2,
      when: "beforeChildren",
    }
  }
};

const itemVariants = {
  closed: { 
    y: 20, 
    opacity: 0,
    transition: {
      duration: 0.3,
      ease: [0.6, 0.05, 0.01, 0.9],
    }
  },
  open: { 
    y: 0, 
    opacity: 1,
    transition: {
      duration: 0.4,
      ease: [0.6, 0.05, 0.01, 0.9],
    }
  }
};

const socialVariants = {
  closed: { 
    y: 20, 
    opacity: 0,
    transition: {
      duration: 0.3,
      ease: [0.6, 0.05, 0.01, 0.9],
    }
  },
  open: { 
    y: 0, 
    opacity: 1,
    transition: {
      duration: 0.4,
      ease: [0.6, 0.05, 0.01, 0.9],
      staggerChildren: 0.1,
      delayChildren: 0.5,
    }
  }
};

const MobileMenu = () => {
  const [isOpen, setIsOpen] = useState(false);
  
  // Предотвращение прокрутки при открытом меню
  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'unset';
    }
    
    // Обработчик клавиши Escape для закрытия меню
    const handleEsc = (e) => {
      if (e.key === 'Escape') {
        setIsOpen(false);
      }
    };
    
    window.addEventListener('keydown', handleEsc);
    
    return () => {
      document.body.style.overflow = 'unset';
      window.removeEventListener('keydown', handleEsc);
    };
  }, [isOpen]);
  
  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };
  
  const closeMenu = () => {
    setIsOpen(false);
  };
  
  const navItems = [
    { name: 'Главная', path: '/' },
    { name: 'Обо мне', path: '/about' },
    { name: 'Проекты', path: '/projects' },
    { name: 'Отзывы', path: '/testimonials' },
    { name: 'Контакты', path: '/contact' }
  ];
  
  return (
    <MenuContainer>
      <MenuButton onClick={toggleMenu} aria-label={isOpen ? 'Закрыть меню' : 'Открыть меню'}>
        <MenuLine
          animate={{
            rotate: isOpen ? 45 : 0,
            y: isOpen ? 8 : 0,
          }}
          transition={{
            duration: 0.3,
            ease: [0.6, 0.05, 0.01, 0.9],
          }}
        />
        <MenuLine
          animate={{
            opacity: isOpen ? 0 : 1,
            x: isOpen ? 20 : 0,
          }}
          transition={{
            duration: 0.3,
            ease: [0.6, 0.05, 0.01, 0.9],
          }}
        />
        <MenuLine
          animate={{
            rotate: isOpen ? -45 : 0,
            y: isOpen ? -8 : 0,
          }}
          transition={{
            duration: 0.3,
            ease: [0.6, 0.05, 0.01, 0.9],
          }}
        />
      </MenuButton>
      
      <AnimatePresence>
        {isOpen && (
          <>
            <MenuBackground
              variants={backgroundVariants}
              initial="closed"
              animate="open"
              exit="closed"
              onClick={closeMenu}
            />
            
            <MenuContent
              variants={containerVariants}
              initial="closed"
              animate="open"
              exit="closed"
            >
              <NavList variants={itemVariants}>
                {navItems.map((item, index) => (
                  <NavItem 
                    key={index}
                    variants={itemVariants}
                  >
                    <NavLink 
                      to={item.path}
                      onClick={closeMenu}
                      whileHover={{ 
                        color: 'var(--accent)',
                        x: 5,
                        transition: { duration: 0.2 }
                      }}
                    >
                      {item.name}
                    </NavLink>
                  </NavItem>
                ))}
              </NavList>
              
              <SocialContainer
                variants={socialVariants}
              >
                <SocialLink 
                  href="https://github.com/username" 
                  target="_blank"
                  rel="noopener noreferrer"
                  variants={itemVariants}
                  whileHover={{ 
                    color: 'var(--accent)',
                    y: -3,
                    transition: { duration: 0.2 }
                  }}
                >
                  <FaGithub />
                </SocialLink>
                <SocialLink 
                  href="https://t.me/username" 
                  target="_blank"
                  rel="noopener noreferrer"
                  variants={itemVariants}
                  whileHover={{ 
                    color: 'var(--accent)',
                    y: -3,
                    transition: { duration: 0.2 }
                  }}
                >
                  <FaTelegram />
                </SocialLink>
                <SocialLink 
                  href="https://linkedin.com/in/username" 
                  target="_blank"
                  rel="noopener noreferrer"
                  variants={itemVariants}
                  whileHover={{ 
                    color: 'var(--accent)',
                    y: -3,
                    transition: { duration: 0.2 }
                  }}
                >
                  <FaLinkedin />
                </SocialLink>
              </SocialContainer>
            </MenuContent>
          </>
        )}
      </AnimatePresence>
    </MenuContainer>
  );
};

export default MobileMenu;
