import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const CursorContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: 9999;
`;

const CursorAscii = styled.pre`
  position: absolute;
  font-family: monospace;
  color: var(--text-primary);
  font-size: 20px;
  line-height: 1;
  margin: 0;
  transform: translate(-50%, -50%);
  pointer-events: none;
  user-select: none;
  opacity: 0.8;
  transition: transform 0.05s ease-out;
`;

// Разные варианты ASCII-курсора
const cursorVariants = [
  `
  ▒▒▒▒
  ▒  ▒
  ▒  ▒
  ▒▒▒▒
  `,
  `
   ▓▓
  ▓▓▓▓
   ▓▓
   ▓▓
  `,
  `
  ╔══╗
  ║  ║
  ╚══╝
  `,
  `
   ▄
  ▄█▄
   ▀
  `
];

const AsciiCursor = () => {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [cursorIndex, setCursorIndex] = useState(0);
  const [isClicking, setIsClicking] = useState(false);

  useEffect(() => {
    // Скрываем стандартный курсор
    document.body.style.cursor = 'none';
    
    const handleMouseMove = (e) => {
      setPosition({ x: e.clientX, y: e.clientY });
    };
    
    const handleMouseDown = () => {
      setIsClicking(true);
      // Меняем вариант курсора при клике
      setCursorIndex((prevIndex) => (prevIndex + 1) % cursorVariants.length);
    };
    
    const handleMouseUp = () => {
      setIsClicking(false);
    };
    
    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('mousedown', handleMouseDown);
    window.addEventListener('mouseup', handleMouseUp);
    
    return () => {
      document.body.style.cursor = 'default';
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mousedown', handleMouseDown);
      window.removeEventListener('mouseup', handleMouseUp);
    };
  }, []);
  
  return (
    <CursorContainer>
      <CursorAscii 
        style={{ 
          left: `${position.x}px`, 
          top: `${position.y}px`,
          transform: isClicking ? 'translate(-50%, -50%) scale(0.8)' : 'translate(-50%, -50%) scale(1)',
        }}
      >
        {cursorVariants[cursorIndex]}
      </CursorAscii>
    </CursorContainer>
  );
};

export default AsciiCursor;
