import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { FaGithub, FaTelegram, FaLinkedinIn } from 'react-icons/fa';

// Создаем контейнер для меню с поддержкой motion
const MenuContainer = styled(motion.div)`
  position: fixed;
  top: 0;
  right: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  z-index: 98;
  pointer-events: ${props => (props.$isOpen ? 'all' : 'none')};
`;

// Фон меню с анимацией
const MenuBackground = styled(motion.div)`
  position: fixed;
  top: 0;
  right: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(10, 25, 47, 0.95);
  backdrop-filter: blur(10px);
  z-index: 98;
`;

// Кнопка меню использует motion для анимаций
const MenuButton = styled.button`
  position: fixed;
  top: 1.8rem;
  right: 1.8rem;
  display: none;
  justify-content: center;
  align-items: center;
  width: 2.5rem;
  height: 2.5rem;
  background: transparent;
  border: none;
  cursor: pointer;
  z-index: 99;
  
  @media (max-width: 768px) {
    display: flex;
  }
`;

// Линии кнопки меню используют motion для анимации
const MenuLine = styled(motion.span)`
  position: absolute;
  width: 1.8rem;
  height: 2px;
  background-color: var(--accent);
  transition: all 0.4s ease;
  
  &:nth-child(1) {
    transform: ${props => props.$isOpen ? 'rotate(45deg)' : 'translateY(-8px)'};
  }
  
  &:nth-child(2) {
    opacity: ${props => props.$isOpen ? 0 : 1};
    transform: ${props => props.$isOpen ? 'translateX(-20px)' : 'translateX(0)'};
  }
  
  &:nth-child(3) {
    transform: ${props => props.$isOpen ? 'rotate(-45deg)' : 'translateY(8px)'};
  }
`;

// Контент меню
const MenuContent = styled(motion.div)`
  position: relative;
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 2rem;
  z-index: 99;
`;

// Список навигации
const NavList = styled(motion.ul)`
  list-style: none;
  padding: 0;
  margin: 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
`;

// Элементы навигации
const NavItem = styled(motion.li)`
  margin: 1rem 0;
  text-align: center;
  width: 100%;
`;

// Ссылки навигации
const NavLink = styled(motion(Link))`
  font-size: 1.5rem;
  font-weight: 600;
  color: var(--text-primary);
  text-decoration: none;
  display: inline-block;
  padding: 0.5rem 1rem;
  position: relative;
  
  &:hover, &.active {
    color: var(--accent);
  }
  
  &:after {
    content: '';
    position: absolute;
    width: 0;
    height: 2px;
    bottom: 0;
    left: 0;
    background-color: var(--accent);
    transition: width 0.4s ease;
  }
  
  &:hover:after, &.active:after {
    width: 100%;
  }
`;

// Контейнер для социальных иконок
const SocialContainer = styled(motion.div)`
  display: flex;
  justify-content: center;
  gap: 1.5rem;
  margin-top: 2rem;
`;

// Соц. иконки
const SocialLink = styled(motion.a)`
  width: 2.5rem;
  height: 2.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 50%;
  border: 1px solid var(--accent);
  color: var(--accent);
  font-size: 1.2rem;
  transition: all 0.4s ease;
  
  &:hover {
    background-color: rgba(100, 255, 218, 0.1);
    transform: translateY(-3px);
  }
`;

// Варианты анимации для фона меню
const backgroundVariants = {
  hidden: { 
    opacity: 0,
    transition: { 
      duration: 0.4, 
      ease: [0.6, 0.05, 0.01, 0.9]
    }
  },
  visible: { 
    opacity: 1,
    transition: { 
      duration: 0.4, 
      ease: [0.6, 0.05, 0.01, 0.9]
    }
  }
};

// Варианты анимации для контейнера меню
const containerVariants = {
  hidden: { 
    x: "100%",
    transition: { 
      duration: 0.4, 
      ease: [0.6, 0.05, 0.01, 0.9]
    }
  },
  visible: { 
    x: 0,
    transition: { 
      duration: 0.4, 
      ease: [0.6, 0.05, 0.01, 0.9],
      staggerChildren: 0.1,
      delayChildren: 0.2
    }
  }
};

// Варианты анимации для навигационных пунктов
const itemVariants = {
  hidden: { 
    y: 20, 
    opacity: 0,
    transition: { 
      duration: 0.3, 
      ease: [0.6, 0.05, 0.01, 0.9]
    }
  },
  visible: { 
    y: 0, 
    opacity: 1,
    transition: { 
      duration: 0.4, 
      ease: [0.6, 0.05, 0.01, 0.9]
    }
  }
};

// Варианты анимации для социальных иконок
const socialVariants = {
  hidden: { 
    y: 20, 
    opacity: 0,
    transition: { 
      duration: 0.3, 
      ease: [0.6, 0.05, 0.01, 0.9]
    }
  },
  visible: { 
    y: 0, 
    opacity: 1,
    transition: { 
      duration: 0.4, 
      ease: [0.6, 0.05, 0.01, 0.9],
      staggerChildren: 0.1,
      delayChildren: 0.5
    }
  }
};

// Варианты анимации для иконок
const iconVariants = {
  hidden: { 
    y: 20, 
    opacity: 0,
    transition: { 
      duration: 0.3, 
      ease: [0.6, 0.05, 0.01, 0.9]
    }
  },
  visible: { 
    y: 0, 
    opacity: 1,
    transition: { 
      duration: 0.4, 
      ease: [0.6, 0.05, 0.01, 0.9]
    }
  },
  hover: {
    y: -3,
    backgroundColor: "rgba(100, 255, 218, 0.1)",
    transition: {
      duration: 0.2,
      ease: "easeInOut"
    }
  }
};

const MobileMenu = ({ isOpen, toggleMenu }) => {
  // Массив навигационных пунктов
  const navItems = [
    { name: 'Главная', path: '/' },
    { name: 'Обо мне', path: '/about' },
    { name: 'Проекты', path: '/projects' },
    { name: 'Отзывы', path: '/testimonials' },
    { name: 'Контакты', path: '/contact' }
  ];
  
  // Массив социальных иконок
  const socialLinks = [
    { icon: <FaGithub />, url: 'https://github.com/username' },
    { icon: <FaTelegram />, url: 'https://t.me/username' },
    { icon: <FaLinkedinIn />, url: 'https://linkedin.com/in/username' }
  ];
  
  // Эффект для блокировки прокрутки при открытом меню
  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'visible';
    }
    
    // Обработчик Escape для закрытия меню
    const handleEscape = (e) => {
      if (e.key === 'Escape' && isOpen) {
        toggleMenu();
      }
    };
    
    window.addEventListener('keydown', handleEscape);
    
    return () => {
      document.body.style.overflow = 'visible';
      window.removeEventListener('keydown', handleEscape);
    };
  }, [isOpen, toggleMenu]);
  
  return (
    <>
      <MenuButton onClick={toggleMenu} aria-label="Мобильное меню">
        <MenuLine $isOpen={isOpen} />
        <MenuLine $isOpen={isOpen} />
        <MenuLine $isOpen={isOpen} />
      </MenuButton>
      
      <AnimatePresence>
        {isOpen && (
          <MenuContainer $isOpen={isOpen}>
            <MenuBackground 
              variants={backgroundVariants}
              initial="hidden"
              animate="visible"
              exit="hidden"
              onClick={toggleMenu}
            />
            
            <MenuContent 
              variants={containerVariants}
              initial="hidden"
              animate="visible"
              exit="hidden"
            >
              <NavList
                variants={itemVariants}
                initial="hidden"
                animate="visible"
              >
                {navItems.map((item, index) => (
                  <NavItem
                    key={index}
                    variants={itemVariants}
                    onClick={toggleMenu}
                  >
                    <NavLink 
                      to={item.path}
                      variants={itemVariants}
                      whileHover={{ color: 'var(--accent)' }}
                    >
                      {item.name}
                    </NavLink>
                  </NavItem>
                ))}
              </NavList>
              
              <SocialContainer
                variants={socialVariants}
                initial="hidden"
                animate="visible"
              >
                {socialLinks.map((social, index) => (
                  <SocialLink 
                    key={index}
                    href={social.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    variants={iconVariants}
                    whileHover="hover"
                    whileTap={{ scale: 0.95 }}
                  >
                    {social.icon}
                  </SocialLink>
                ))}
              </SocialContainer>
            </MenuContent>
          </MenuContainer>
        )}
      </AnimatePresence>
    </>
  );
};

export default MobileMenu;
