import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

const TestimonialsContainer = styled.div`
  min-height: 100vh;
  padding-top: 100px;
  padding-bottom: 50px;
`;

const SectionTitle = styled.h1`
  font-size: clamp(2rem, 5vw, 3rem);
  text-align: center;
  margin-bottom: 60px;
  letter-spacing: -1px;
  
  .accent {
    display: inline-block;
    position: relative;
    
    &:after {
      content: "";
      position: absolute;
      bottom: 0;
      left: 0;
      width: 100%;
      height: 1px;
      background-color: var(--text-primary);
    }
  }
`;

const TestimonialsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 30px;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const TestimonialAsciiFrame = styled.pre`
  font-family: monospace;
  font-size: 10px;
  white-space: pre;
  line-height: 1;
  color: var(--text-primary);
  width: 100%;
  height: 100%;
  pointer-events: none;
  user-select: none;
  margin: 0;
  position: absolute;
  top: 0;
  left: 0;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const TestimonialCard = styled(motion.div)`
  position: relative;
  cursor: pointer;
  padding: 30px;
  min-height: 250px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background-color: rgba(30, 30, 30, 0.3);
  transition: all 0.3s ease;
  overflow: hidden;
  
  &:hover {
    background-color: rgba(40, 40, 40, 0.5);
    transform: translateY(-5px);
    
    .ascii-frame {
      opacity: 1;
    }
  }
  
  .ascii-frame {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    opacity: 0.3;
    transition: opacity 0.3s ease;
    pointer-events: none;
    z-index: 0;
  }
`;

const TestimonialContent = styled.div`
  position: relative;
  z-index: 1;
  text-align: center;
`;

const Quote = styled.blockquote`
  font-size: 1.1rem;
  line-height: 1.6;
  margin-bottom: 15px;
  max-width: 500px;
  font-style: italic;
`;

const Author = styled.cite`
  display: block;
  font-size: 0.9rem;
  color: var(--text-secondary);
  font-style: normal;
  margin-top: 15px;
  
  .company {
    display: block;
    margin-top: 5px;
    font-size: 0.8rem;
  }
`;

const ModalBackdrop = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.8);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
  padding: 2rem;
`;

const ModalContent = styled(motion.div)`
  max-width: 700px;
  width: 100%;
  background-color: var(--main-bg);
  padding: 2rem;
  position: relative;
  border: 1px solid var(--border);
  
  .quote {
    font-size: 1.3rem;
    line-height: 1.7;
    margin-bottom: 1.5rem;
  }
  
  .author {
    font-size: 1.1rem;
  }
`;

const CloseButton = styled.button`
  position: absolute;
  top: 10px;
  right: 10px;
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: var(--text-primary);
`;

// Рамка из ASCII-символов - динамические рамки
const generateAsciiFrame = (type) => {
  // Коды для верхних, боковых и угловых элементов 
  // в зависимости от типа рамки
  let topLeft, topRight, bottomLeft, bottomRight, horizontal, vertical;
  
  switch(type) {
    case 0:
      topLeft = '+'; topRight = '+'; 
      bottomLeft = '+'; bottomRight = '+';
      horizontal = '-'; vertical = '|';
      break;
    case 1:
      topLeft = '╔'; topRight = '╗'; 
      bottomLeft = '╚'; bottomRight = '╝';
      horizontal = '═'; vertical = '║';
      break;
    case 2:
      topLeft = '┌'; topRight = '┐'; 
      bottomLeft = '└'; bottomRight = '┘';
      horizontal = '─'; vertical = '│';
      break;
    case 3:
      topLeft = '▛'; topRight = '▜'; 
      bottomLeft = '▙'; bottomRight = '▟';
      horizontal = '▀'; vertical = '▐';
      break;
    default:
      topLeft = '+'; topRight = '+'; 
      bottomLeft = '+'; bottomRight = '+';
      horizontal = '-'; vertical = '|';
  }
  
  return {
    top: (width) => topLeft + horizontal.repeat(width - 2) + topRight,
    middle: (width) => vertical + ' '.repeat(width - 2) + vertical,
    bottom: (width) => bottomLeft + horizontal.repeat(width - 2) + bottomRight
  };
};

// Данные отзывов
const testimonials = [
  {
    id: 1,
    quote: "Работа с этим креативным дизайнером превзошла все наши ожидания. Уникальный взгляд на проблему и инновационные решения определенно выделили наш бренд.",
    author: "Елена Волкова",
    company: "CEO, Bright Future",
    frameIndex: 0
  },
  {
    id: 2,
    quote: "Невероятное внимание к деталям и способность улавливать самую суть бренда. Результаты работы не только визуально привлекательны, но и потрясающе функциональны.",
    author: "Алексей Морозов",
    company: "Marketing Director, Синергия",
    frameIndex: 1
  },
  {
    id: 3,
    quote: "Я сотрудничал с многими дизайнерами, но этот опыт был особенным. Проект был выполнен в срок, с глубоким пониманием наших целей и полным учетом наших пожеланий.",
    author: "Мария Сергеева",
    company: "Project Manager, DigitalWave",
    frameIndex: 2
  },
  {
    id: 4,
    quote: "Тонкое чувство стиля и современных тенденций. Креативные решения, которые предложил дизайнер, помогли нашему продукту выделиться на рынке и привлечь новую аудиторию.",
    author: "Дмитрий Казаков",
    company: "Product Owner, TechSolutions",
    frameIndex: 3
  },
  {
    id: 5,
    quote: "Профессионализм и креативность на высшем уровне. Проект был сложным, но результат превзошел все наши ожидания. Обязательно будем сотрудничать снова.",
    author: "Ольга Соколова",
    company: "Art Director, Creative Studio",
    frameIndex: 0
  },
  {
    id: 6,
    quote: "Редко встречаются специалисты, которые так глубоко погружаются в проект и предлагают действительно инновационные решения. Наш ребрендинг получил множество положительных отзывов.",
    author: "Антон Лебедев",
    company: "CEO, Momentum",
    frameIndex: 1
  }
];

const Testimonials = () => {
  const [selectedTestimonial, setSelectedTestimonial] = useState(null);
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });
  
  const openModal = (testimonial) => {
    setSelectedTestimonial(testimonial);
    document.body.style.overflow = 'hidden'; // Блокировка прокрутки фона
  };
  
  const closeModal = () => {
    setSelectedTestimonial(null);
    document.body.style.overflow = 'auto'; // Возобновление прокрутки
  };
  
  const cardVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: (i) => ({
      opacity: 1,
      y: 0,
      transition: {
        delay: i * 0.1,
        duration: 0.5
      }
    })
  };
  
  return (
    <TestimonialsContainer>
      <SectionTitle>
        <span className="accent">Отзывы</span> клиентов
      </SectionTitle>
      
      <TestimonialsGrid ref={ref}>
        {testimonials.map((testimonial, index) => {
          const frame = generateAsciiFrame(testimonial.frameIndex);
          return (
            <TestimonialCard
              key={testimonial.id}
              variants={cardVariants}
              initial="hidden"
              animate={inView ? "visible" : "hidden"}
              custom={index}
              onClick={() => openModal(testimonial)}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              <TestimonialAsciiFrame className="ascii-frame">
                {frame.top(40)}
                {frame.middle(40)}
                {frame.middle(40)}
                {frame.middle(40)}
                {frame.middle(40)}
                {frame.middle(40)}
                {frame.middle(40)}
                {frame.middle(40)}
                {frame.middle(40)}
                {frame.middle(40)}
                {frame.bottom(40)}
              </TestimonialAsciiFrame>
              
              <TestimonialContent>
                <Quote>"{testimonial.quote.length > 120 ? testimonial.quote.substring(0, 120) + '...' : testimonial.quote}"</Quote>
                <Author>
                  {testimonial.author}
                  <span className="company">{testimonial.company}</span>
                </Author>
              </TestimonialContent>
            </TestimonialCard>
          );
        })}
      </TestimonialsGrid>
      
      <AnimatePresence>
        {selectedTestimonial && (
          <ModalBackdrop
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={closeModal}
          >
            <ModalContent
              onClick={(e) => e.stopPropagation()}
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.8, opacity: 0 }}
            >
              <CloseButton onClick={closeModal}>×</CloseButton>
              <Quote className="quote">"{selectedTestimonial.quote}"</Quote>
              <Author className="author">
                {selectedTestimonial.author}
                <span className="company">{selectedTestimonial.company}</span>
              </Author>
            </ModalContent>
          </ModalBackdrop>
        )}
      </AnimatePresence>
    </TestimonialsContainer>
  );
};

export default Testimonials;
