import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import { motion, useAnimation, AnimatePresence } from 'framer-motion';
import MobileMenu from './MobileMenu';
import logo from '../assets/logo.svg';

// Компонент контейнера заголовка с динамическими стилями на основе состояния
const HeaderContainer = styled(motion.header)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  z-index: 100;
  padding: ${({ $isScrolled }) => ($isScrolled ? '0.8rem 1.5rem' : '1.2rem 1.5rem')};
  transition: all 0.3s ease;
  backdrop-filter: ${({ $isScrolled }) => ($isScrolled ? 'blur(10px)' : 'blur(0px)')};
  background-color: ${({ $isScrolled, $isMenuOpen }) => 
    $isMenuOpen 
      ? 'transparent' 
      : $isScrolled 
        ? 'rgba(var(--header-bg-rgb), 0.85)' 
        : 'rgba(var(--header-bg-rgb), 0)'
  };
  box-shadow: ${({ $isScrolled, $isMenuOpen }) => 
    $isMenuOpen 
      ? 'none' 
      : $isScrolled 
        ? '0 4px 20px rgba(0, 0, 0, 0.1)' 
        : 'none'
  };

  @media (max-width: 768px) {
    padding: ${({ $isScrolled }) => ($isScrolled ? '0.6rem 1rem' : '1rem')};
  }
`;

const HeaderWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  max-width: var(--max-width);
  margin: 0 auto;
`;

const LogoContainer = styled(motion.div)`
  display: flex;
  align-items: center;
  z-index: 200;
`;

const LogoLink = styled(Link)`
  text-decoration: none;
  display: block;
`;

const Logo = styled.img`
  height: 45px;

  @media (max-width: 768px) {
    height: 40px;
  }
`;

const NavContainer = styled.nav`
  display: flex;
  align-items: center;

  @media (max-width: 768px) {
    display: none;
  }
`;

const NavList = styled.ul`
  display: flex;
  list-style: none;
  margin: 0;
  padding: 0;
  gap: 2rem;
`;

const NavItem = styled(motion.li)`
  margin: 0;
`;

const NavLink = styled(motion.div)`
  position: relative;

  a {
    text-decoration: none;
    color: var(--text-primary);
    font-weight: 500;
    font-size: 1rem;
    letter-spacing: 0.5px;
    padding: 0.5rem;
    transition: color 0.3s ease;

    &:hover {
      color: var(--text-highlight);
    }

    &.active {
      color: var(--text-highlight);
    }
  }

  &:after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 0;
    height: 2px;
    background-color: var(--text-highlight);
    transition: width 0.3s ease;
  }

  &:hover:after,
  a.active + &:after {
    width: 100%;
  }
`;

const ActionButton = styled(motion.button)`
  background-color: var(--button-primary);
  color: var(--text-on-primary);
  border: none;
  padding: 0.6rem 1.2rem;
  border-radius: 4px;
  font-weight: 600;
  font-size: 0.9rem;
  letter-spacing: 0.5px;
  margin-left: 2rem;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: 0 4px 10px rgba(var(--button-primary-rgb), 0.3);

  &:hover {
    background-color: var(--button-primary-hover);
    box-shadow: 0 6px 15px rgba(var(--button-primary-rgb), 0.4);
    transform: translateY(-2px);
  }

  &:active {
    transform: translateY(0);
    box-shadow: 0 2px 5px rgba(var(--button-primary-rgb), 0.3);
  }

  &:focus {
    outline: none;
    box-shadow: 0 0 0 3px rgba(var(--button-primary-rgb), 0.3), 0 4px 10px rgba(var(--button-primary-rgb), 0.3);
  }
`;

// Анимационные варианты с исправленной кривой Безье
const logoVariants = {
  hidden: { 
    opacity: 0, 
    y: -20,
    transition: {
      duration: 0.5,
      ease: [0.6, 0.05, 0, 0.9]
    }
  },
  visible: { 
    opacity: 1, 
    y: 0,
    transition: {
      duration: 0.5,
      ease: [0.6, 0.05, 0, 0.9]
    }
  }
};

const navItemVariants = {
  hidden: i => ({ 
    opacity: 0, 
    y: -20,
    transition: {
      duration: 0.5,
      ease: [0.6, 0.05, 0, 0.9]
    }
  }),
  visible: i => ({ 
    opacity: 1, 
    y: 0,
    transition: {
      delay: 0.1 + i * 0.1,
      duration: 0.5,
      ease: [0.6, 0.05, 0, 0.9]
    }
  })
};

const buttonVariants = {
  hidden: { 
    opacity: 0, 
    y: -20,
    transition: {
      duration: 0.5,
      ease: [0.6, 0.05, 0, 0.9]
    }
  },
  visible: { 
    opacity: 1, 
    y: 0,
    transition: {
      delay: 0.5,
      duration: 0.5,
      ease: [0.6, 0.05, 0, 0.9]
    }
  },
  tap: { 
    scale: 0.95,
    transition: {
      duration: 0.1,
      ease: [0.6, 0.05, 0, 0.9]
    }
  },
  hover: { 
    y: -5,
    transition: {
      duration: 0.3,
      ease: [0.6, 0.05, 0, 0.9]
    }
  }
};

const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const location = useLocation();
  const controls = useAnimation();

  // Эффект для отслеживания скролла и запуска анимации
  useEffect(() => {
    const handleScroll = () => {
      const offset = window.scrollY;
      setIsScrolled(offset > 50);
    };

    window.addEventListener('scroll', handleScroll);

    controls.start('visible');

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [controls]);

  // Функция переключения мобильного меню
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  // Массив навигационных пунктов
  const navItems = [
    { name: 'Главная', path: '/' },
    { name: 'Обо мне', path: '/about' },
    { name: 'Проекты', path: '/projects' },
    { name: 'Отзывы', path: '/testimonials' },
    { name: 'Контакты', path: '/contact' }
  ];

  return (
    <HeaderContainer 
      $isScrolled={isScrolled} 
      $isMenuOpen={isMenuOpen}
      initial="hidden"
      animate={controls}
    >
      <HeaderWrapper>
        <LogoContainer
          variants={logoVariants}
        >
          <LogoLink to="/">
            <Logo src={logo} alt="Логотип" />
          </LogoLink>
        </LogoContainer>

        <NavContainer>
          <NavList>
            {navItems.map((item, index) => (
              <NavItem 
                key={item.path}
                custom={index}
                variants={navItemVariants}
              >
                <NavLink
                  whileHover={{ y: -2 }}
                  whileTap={{ y: 0 }}
                >
                  <Link 
                    to={item.path}
                    className={location.pathname === item.path ? 'active' : ''}
                  >
                    {item.name}
                  </Link>
                </NavLink>
              </NavItem>
            ))}
          </NavList>

          <ActionButton
            variants={buttonVariants}
            whileHover="hover"
            whileTap="tap"
          >
            Связаться
          </ActionButton>
        </NavContainer>

        <MobileMenu isOpen={isMenuOpen} toggleMenu={toggleMenu} />
      </HeaderWrapper>
    </HeaderContainer>
  );
};

export default Header;
