import React, { useEffect, useRef } from 'react';
import { Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { FaGithub, FaTelegram, FaLinkedinIn } from 'react-icons/fa';

// Фоновые элементы для создания визуальных эффектов
const Background = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background: linear-gradient(135deg, var(--main-bg), var(--main-bg-darker));
  z-index: 149;
`;

const BackgroundPattern = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-image: 
    radial-gradient(circle at 25% 25%, rgba(255, 255, 255, 0.03) 1px, transparent 1px),
    radial-gradient(circle at 75% 75%, rgba(255, 255, 255, 0.03) 1px, transparent 1px);
  background-size: 50px 50px;
  z-index: 150;
`;

const MobileMenuContainer = styled.div`
  display: none;
  
  @media (max-width: 768px) {
    display: block;
  }
`;

const MenuButton = styled.button`
  background: transparent;
  border: none;
  padding: 10px;
  cursor: pointer;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  width: 35px;
  height: 35px;
  position: relative;
  z-index: 200;
  transition: all 0.3s ease;
  
  &:focus {
    outline: none;
  }
  
  &:hover {
    transform: scale(1.1);
  }
`;

const MenuLine = styled(motion.div)`
  width: 100%;
  height: 2px;
  background-color: var(--text-primary);
  border-radius: 2px;
  transition: all 0.3s ease;
`;

const MenuOverlay = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 199;
  overflow: hidden;
  touch-action: none;
`;

const MenuContent = styled(motion.div)`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 2rem;
  box-sizing: border-box;
  touch-action: none;
`;

const MenuNav = styled(motion.nav)`
  text-align: center;
  margin-bottom: 2rem;
  width: 100%;
`;

const MenuList = styled(motion.ul)`
  list-style: none;
  padding: 0;
  margin: 0;
  display: flex;
  flex-direction: column;
  gap: 2rem;
  width: 100%;
`;

const MenuItem = styled(motion.li)`
  margin: 0;
  padding: 0;
  position: relative;
`;

const MenuLink = styled(motion(Link))`
  font-size: 2rem;
  font-weight: 600;
  letter-spacing: 1px;
  text-transform: uppercase;
  padding: 0.5rem 1rem;
  color: var(--text-primary);
  text-decoration: none;
  position: relative;
  display: inline-block;
  transition: all 0.3s ease;
  
  &:hover {
    color: var(--text-highlight);
    transform: translateX(5px) scale(1.05);
    text-shadow: 0 0 10px rgba(var(--text-highlight-rgb), 0.3);
  }
  
  &:active {
    transform: translateX(2px) scale(0.98);
  }
  
  &:after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 0;
    height: 2px;
    background-color: var(--text-highlight);
    transition: width 0.3s ease;
  }
  
  &:hover:after {
    width: 100%;
  }
  
  &.active {
    color: var(--text-highlight);
    
    &:after {
      width: 100%;
    }
  }
`;

const Socials = styled(motion.div)`
  display: flex;
  gap: 1.5rem;
  margin-top: 2rem;
`;

const SocialLink = styled(motion.a)`
  color: var(--text-primary);
  font-size: 1.5rem;
  transition: all 0.3s ease;
  
  &:hover {
    color: var(--text-highlight);
    transform: translateY(-3px);
  }
  
  &:active {
    transform: translateY(0);
  }
`;

// Анимационные варианты с исправленной кривой Безье
const overlayVariants = {
  closed: {
    opacity: 0,
    pointerEvents: 'none',
    transition: {
      when: 'afterChildren',
      staggerChildren: 0.05,
      staggerDirection: -1,
      duration: 0.3,
      ease: [0.6, 0.05, 0, 0.9]
    }
  },
  open: {
    opacity: 1,
    pointerEvents: 'auto',
    transition: {
      when: 'beforeChildren',
      staggerChildren: 0.1,
      delayChildren: 0.1,
      duration: 0.4,
      ease: [0.6, 0.05, 0, 0.9]
    }
  }
};

const contentVariants = {
  closed: {
    opacity: 0,
    y: 50,
    transition: {
      duration: 0.3,
      ease: [0.6, 0.05, 0, 0.9]
    }
  },
  open: {
    opacity: 1,
    y: 0,
    transition: {
      duration: 0.4,
      ease: [0.6, 0.05, 0, 0.9]
    }
  }
};

const itemVariants = {
  closed: {
    opacity: 0,
    y: 20,
    transition: {
      duration: 0.3,
      ease: [0.6, 0.05, 0, 0.9]
    }
  },
  open: i => ({
    opacity: 1,
    y: 0,
    transition: {
      delay: i * 0.1,
      duration: 0.4,
      ease: [0.6, 0.05, 0, 0.9]
    }
  })
};

const backgroundVariants = {
  closed: {
    opacity: 0,
    scale: 0.9,
    transition: {
      duration: 0.4,
      ease: [0.6, 0.05, 0, 0.9]
    }
  },
  open: {
    opacity: 1,
    scale: 1,
    transition: {
      duration: 0.5,
      ease: [0.6, 0.05, 0, 0.9]
    }
  }
};

const patternVariants = {
  closed: {
    opacity: 0,
    scale: 1.1,
    transition: {
      duration: 0.3,
      ease: [0.6, 0.05, 0, 0.9]
    }
  },
  open: {
    opacity: 0.5,
    scale: 1,
    transition: {
      duration: 0.6,
      ease: [0.6, 0.05, 0, 0.9]
    }
  }
};

const lineOneVariants = {
  closed: { 
    rotate: 0, 
    translateY: 0,
    transition: {
      duration: 0.4,
      ease: [0.6, 0.05, 0, 0.9]
    }
  },
  open: { 
    rotate: 45, 
    translateY: 10,
    transition: {
      duration: 0.4,
      ease: [0.6, 0.05, 0, 0.9]
    }
  }
};

const lineTwoVariants = {
  closed: { 
    opacity: 1,
    transition: {
      duration: 0.4,
      ease: [0.6, 0.05, 0, 0.9]
    }
  },
  open: { 
    opacity: 0,
    transition: {
      duration: 0.2,
      ease: [0.6, 0.05, 0, 0.9]
    }
  }
};

const lineThreeVariants = {
  closed: { 
    rotate: 0, 
    translateY: 0,
    transition: {
      duration: 0.4,
      ease: [0.6, 0.05, 0, 0.9]
    }
  },
  open: { 
    rotate: -45, 
    translateY: -10,
    transition: {
      duration: 0.4,
      ease: [0.6, 0.05, 0, 0.9]
    }
  }
};

const MobileMenu = ({ isOpen, toggleMenu }) => {
  const location = useLocation();
  const menuRef = useRef(null);

  // Блокировка скролла при открытом меню
  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = 'hidden';
      document.body.style.position = 'fixed';
      document.body.style.width = '100%';
      document.body.style.height = '100%';
      document.body.style.touchAction = 'none';
      document.documentElement.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = '';
      document.body.style.position = '';
      document.body.style.width = '';
      document.body.style.height = '';
      document.body.style.touchAction = '';
      document.documentElement.style.overflow = '';
    }
    
    return () => {
      document.body.style.overflow = '';
      document.body.style.position = '';
      document.body.style.width = '';
      document.body.style.height = '';
      document.body.style.touchAction = '';
      document.documentElement.style.overflow = '';
    };
  }, [isOpen]);
  
  // Обработка нажатия клавиши Escape для закрытия меню
  useEffect(() => {
    const handleEscapeKey = (e) => {
      if (e.key === 'Escape' && isOpen) {
        toggleMenu();
      }
    };
    
    window.addEventListener('keydown', handleEscapeKey);
    
    return () => {
      window.removeEventListener('keydown', handleEscapeKey);
    };
  }, [isOpen, toggleMenu]);

  // Предотвращение пропуска нажатий внутри меню
  const handleMenuClick = (e) => {
    if (isOpen) {
      e.stopPropagation();
    }
  };

  return (
    <MobileMenuContainer ref={menuRef}>
      <MenuButton onClick={toggleMenu} aria-label={isOpen ? 'Закрыть меню' : 'Открыть меню'}>
        <MenuLine 
          variants={lineOneVariants}
          initial="closed"
          animate={isOpen ? 'open' : 'closed'}
        />
        <MenuLine 
          variants={lineTwoVariants}
          initial="closed"
          animate={isOpen ? 'open' : 'closed'}
        />
        <MenuLine 
          variants={lineThreeVariants}
          initial="closed"
          animate={isOpen ? 'open' : 'closed'}
        />
      </MenuButton>
      
      <AnimatePresence>
        {isOpen && (
          <>
            <Background
              variants={backgroundVariants}
              initial="closed"
              animate="open"
              exit="closed"
            />
            
            <BackgroundPattern
              variants={patternVariants}
              initial="closed"
              animate="open"
              exit="closed"
            />
            
            <MenuOverlay
              variants={overlayVariants}
              initial="closed"
              animate="open"
              exit="closed"
              onClick={handleMenuClick}
            >
              <MenuContent
                variants={contentVariants}
              >
                <MenuNav>
                  <MenuList>
                    <MenuItem custom={0} variants={itemVariants}>
                      <MenuLink 
                        to="/" 
                        onClick={toggleMenu}
                        className={location.pathname === '/' ? 'active' : ''}
                        whileHover={{ x: 5 }}
                        whileTap={{ scale: 0.95 }}
                      >
                        Главная
                      </MenuLink>
                    </MenuItem>
                    
                    <MenuItem custom={1} variants={itemVariants}>
                      <MenuLink 
                        to="/about" 
                        onClick={toggleMenu}
                        className={location.pathname === '/about' ? 'active' : ''}
                        whileHover={{ x: 5 }}
                        whileTap={{ scale: 0.95 }}
                      >
                        Обо мне
                      </MenuLink>
                    </MenuItem>
                    
                    <MenuItem custom={2} variants={itemVariants}>
                      <MenuLink 
                        to="/projects" 
                        onClick={toggleMenu}
                        className={location.pathname === '/projects' ? 'active' : ''}
                        whileHover={{ x: 5 }}
                        whileTap={{ scale: 0.95 }}
                      >
                        Проекты
                      </MenuLink>
                    </MenuItem>
                    
                    <MenuItem custom={3} variants={itemVariants}>
                      <MenuLink 
                        to="/testimonials" 
                        onClick={toggleMenu}
                        className={location.pathname === '/testimonials' ? 'active' : ''}
                        whileHover={{ x: 5 }}
                        whileTap={{ scale: 0.95 }}
                      >
                        Отзывы
                      </MenuLink>
                    </MenuItem>
                    
                    <MenuItem custom={4} variants={itemVariants}>
                      <MenuLink 
                        to="/contact" 
                        onClick={toggleMenu}
                        className={location.pathname === '/contact' ? 'active' : ''}
                        whileHover={{ x: 5 }}
                        whileTap={{ scale: 0.95 }}
                      >
                        Контакты
                      </MenuLink>
                    </MenuItem>
                  </MenuList>
                </MenuNav>
                
                <Socials
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ 
                    opacity: 1, 
                    y: 0,
                    transition: { delay: 0.7, duration: 0.4 }
                  }}
                  exit={{ opacity: 0, y: 20 }}
                >
                  <SocialLink 
                    href="https://github.com" 
                    target="_blank" 
                    rel="noopener noreferrer"
                    whileHover={{ y: -5 }}
                    whileTap={{ y: 0 }}
                  >
                    <FaGithub />
                  </SocialLink>
                  
                  <SocialLink 
                    href="https://t.me" 
                    target="_blank" 
                    rel="noopener noreferrer"
                    whileHover={{ y: -5 }}
                    whileTap={{ y: 0 }}
                  >
                    <FaTelegram />
                  </SocialLink>
                  
                  <SocialLink 
                    href="https://linkedin.com" 
                    target="_blank" 
                    rel="noopener noreferrer"
                    whileHover={{ y: -5 }}
                    whileTap={{ y: 0 }}
                  >
                    <FaLinkedinIn />
                  </SocialLink>
                </Socials>
              </MenuContent>
            </MenuOverlay>
          </>
        )}
      </AnimatePresence>
    </MobileMenuContainer>
  );
};

export default MobileMenu;
