import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import { motion, useAnimation } from 'framer-motion';
import MobileMenu from './MobileMenu';
import logo from '../assets/images/header/Lolo_tunk_1.svg';

// Создаем HeaderContainer с корректной обработкой скролла
const HeaderContainer = styled(motion.header)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  padding: ${props => props.$isScrolled ? '1rem 2rem' : '1.5rem 2rem'};
  display: flex;
  justify-content: space-between;
  align-items: center;
  z-index: 100;
  transition: all 0.4s ease;
  background-color: ${props => 
    props.$isScrolled 
      ? 'rgba(10, 25, 47, 0.85)' 
      : 'transparent'
  };
  backdrop-filter: ${props => 
    props.$isScrolled ? 'blur(10px)' : 'none'
  };
  box-shadow: ${props => 
    props.$isScrolled ? '0 10px 30px -10px rgba(2, 12, 27, 0.7)' : 'none'
  };

  @media (max-width: 768px) {
    padding: ${props => props.$isScrolled ? '0.8rem 1.5rem' : '1rem 1.5rem'};
  }
`;

// Создаем компоненты с использованием motion для анимаций
const LogoContainer = styled(motion.div)`
  display: flex;
  align-items: center;
`;

const Logo = styled(motion.img)`
  height: 45px;

  @media (max-width: 768px) {
    height: 35px;
  }
`;

const NavContainer = styled.nav`
  display: flex;
  align-items: center;

  @media (max-width: 768px) {
    display: none;
  }
`;

const NavList = styled.ul`
  display: flex;
  gap: 2rem;
  list-style: none;
  margin: 0;
  padding: 0;
`;

const NavItem = styled(motion.li)`
  position: relative;
`;

const NavLink = styled(motion(Link))`
  color: var(--text-primary);
  text-decoration: none;
  font-weight: 500;
  padding: 0.5rem;
  position: relative;
  transition: color 0.3s ease;

  &:hover, &.active {
    color: var(--accent);
  }

  &::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 0;
    height: 2px;
    background-color: var(--accent);
    transition: width 0.4s ease;
  }

  &:hover::after, &.active::after {
    width: 100%;
  }
`;

const ActionButton = styled(motion.button)`
  background: transparent;
  border: 1px solid var(--accent);
  color: var(--accent);
  padding: 0.6rem 1.2rem;
  border-radius: 4px;
  font-weight: 500;
  font-size: 0.9rem;
  cursor: pointer;
  margin-left: 2rem;
  transition: all 0.3s ease;

  &:hover {
    background-color: rgba(100, 255, 218, 0.1);
    transform: translateY(-3px);
  }
`;

// Добавляю стиль NavLinks с медиа-запросом
const NavLinks = styled.div`
  display: flex;
  gap: 2rem;

  @media (max-width: 768px) {
    display: none;
  }
`;

// Компонент MobileMenuButton для мобильных устройств
const MobileMenuButton = styled.div`
  display: none;

  @media (max-width: 768px) {
    display: block;
  }
`;

// Правильные варианты анимации
const logoVariants = {
  hidden: { opacity: 0, y: -20 },
  visible: { 
    opacity: 1, 
    y: 0,
    transition: { 
      duration: 0.5, 
      ease: [0.6, 0.05, 0.01, 0.9]
    }
  }
};

const navItemVariants = {
  hidden: { opacity: 0, y: -20 },
  visible: i => ({ 
    opacity: 1, 
    y: 0,
    transition: { 
      delay: i * 0.1 + 0.2,
      duration: 0.5, 
      ease: [0.6, 0.05, 0.01, 0.9]
    }
  })
};

const buttonVariants = {
  hidden: { opacity: 0, y: -20 },
  visible: { 
    opacity: 1, 
    y: 0,
    transition: { 
      delay: 0.6,
      duration: 0.5, 
      ease: [0.6, 0.05, 0.01, 0.9]
    }
  },
  hover: {
    y: -5,
    backgroundColor: "rgba(100, 255, 218, 0.1)",
    transition: {
      duration: 0.2,
      ease: "easeInOut"
    }
  }
};

const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const location = useLocation();
  const controls = useAnimation();

  // Функция переключения состояния мобильного меню
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  // Обрабатываем событие скролла страницы
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setIsScrolled(true);
      } else {
        setIsScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    // Запускаем анимацию при монтировании
    controls.start({ y: 0, transition: { duration: 0.5, ease: "easeOut" } });

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [controls]);

  return (
    <HeaderContainer 
      $isScrolled={isScrolled}
      initial={{ y: -100 }}
      animate={controls}
    >
      <LogoContainer
        variants={logoVariants}
        initial="hidden"
        animate="visible"
      >
        <Link to="/">
          <Logo 
            src={logo} 
            alt="Логотип" 
            variants={logoVariants}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          />
        </Link>
      </LogoContainer>

      <NavLinks>
        <NavItem>
          <NavLink 
            to="/" 
            className={location.pathname === '/' ? 'active' : ''}
          >
            Главная
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink 
            to="/about" 
            className={location.pathname === '/about' ? 'active' : ''}
          >
            Обо мне
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink 
            to="/projects" 
            className={location.pathname === '/projects' ? 'active' : ''}
          >
            Проекты
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink 
            to="/testimonials" 
            className={location.pathname === '/testimonials' ? 'active' : ''}
          >
            Отзывы
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink 
            to="/contact" 
            className={location.pathname === '/contact' ? 'active' : ''}
          >
            Контакты
          </NavLink>
        </NavItem>

        <ActionButton 
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          Резюме
        </ActionButton>
      </NavLinks>

      <MobileMenuButton>
        <MobileMenu isOpen={isMenuOpen} toggleMenu={toggleMenu} />
      </MobileMenuButton>
    </HeaderContainer>
  );
};

export default Header;
