import React, { useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';

const MobileMenuContainer = styled.div`
  display: none;
  
  @media (max-width: 768px) {
    display: block;
  }
`;

const MenuButton = styled.button`
  background: transparent;
  border: none;
  width: 44px;
  height: 44px;
  padding: 8px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  position: relative;
  z-index: 200;
  cursor: pointer;
  border-radius: 50%;
  transition: all 0.3s ease;
  
  &:hover {
    background-color: rgba(255, 255, 255, 0.05);
  }
  
  &:active {
    background-color: rgba(255, 255, 255, 0.1);
    transform: scale(0.95);
  }
`;

const MenuLine = styled.span`
  display: block;
  width: 24px;
  height: 2px;
  margin: 3px 0;
  background-color: var(--text-primary);
  transition: all 0.3s ease;
  
  &:nth-child(1) {
    transform: ${props => props.$isOpen ? 'rotate(45deg) translate(5px, 5px)' : 'none'};
  }
  
  &:nth-child(2) {
    opacity: ${props => props.$isOpen ? 0 : 1};
    transform: ${props => props.$isOpen ? 'translateX(-10px)' : 'none'};
  }
  
  &:nth-child(3) {
    transform: ${props => props.$isOpen ? 'rotate(-45deg) translate(5px, -5px)' : 'none'};
  }
`;

const MenuOverlay = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  width: 100vw;
  height: 100vh;
  background: rgba(10, 10, 10, 0.98);
  z-index: 150;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 0;
  overflow: hidden;
`;

const Background = styled(motion.div)`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  width: 100%;
  height: 100%;
  background: radial-gradient(circle at center, rgba(40, 40, 40, 0.3) 0%, rgba(10, 10, 10, 0) 70%);
  z-index: -1;
  pointer-events: none;
`;

const BackgroundPattern = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  width: 100%;
  height: 100%;
  background-image: 
    linear-gradient(rgba(255, 255, 255, 0.02) 1px, transparent 1px),
    linear-gradient(90deg, rgba(255, 255, 255, 0.02) 1px, transparent 1px);
  background-size: 30px 30px;
  z-index: -1;
  pointer-events: none;
  opacity: 0.5;
`;

const MenuContent = styled(motion.div)`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 100%;
  max-width: 100vw;
  overflow: hidden;
  padding: 0 1rem;
  z-index: 1;
`;

const MenuList = styled(motion.ul)`
  list-style: none;
  padding: 0;
  margin: 0;
  width: 100%;
  max-width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const MenuItem = styled(motion.li)`
  margin: 1.2rem 0;
  text-align: center;
  overflow: hidden;
  width: 100%;
  max-width: 100%;
  perspective: 1000px;
`;

const MenuLink = styled(Link)`
  font-size: 2.4rem;
  letter-spacing: 1px;
  text-transform: uppercase;
  color: var(--text-primary);
  text-decoration: none;
  position: relative;
  display: inline-block;
  padding: 0.5rem 1rem;
  font-weight: 500;
  transition: all 0.3s ease;
  
  &:after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 0;
    height: 2px;
    background-color: var(--text-primary);
    transition: width 0.3s ease;
  }
  
  &.active {
    color: #fff;
    text-shadow: 0 0 10px rgba(255, 255, 255, 0.3);
    
    &:after {
      width: 100%;
    }
  }
  
  &:hover {
    transform: translateY(-2px);
    color: #fff;
    text-shadow: 0 0 10px rgba(255, 255, 255, 0.3);
    
    &:after {
      width: 100%;
    }
  }
  
  &:active {
    transform: translateY(0);
  }
`;

const MenuFooter = styled(motion.div)`
  width: 100%;
  padding: 2rem 0;
  font-size: 0.9rem;
  color: var(--text-secondary);
  text-align: center;
  margin-top: auto;
`;

const SocialLinks = styled(motion.div)`
  display: flex;
  justify-content: center;
  gap: 1.5rem;
  margin-top: 2rem;
  width: 100%;
`;

const SocialLink = styled(motion.a)`
  display: inline-block;
  width: 44px;
  height: 44px;
  border: 1px solid var(--border);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  color: var(--text-primary);
  font-size: 0.9rem;
  letter-spacing: 0.5px;
  
  &:hover {
    background-color: var(--text-primary);
    color: var(--main-bg);
    transform: translateY(-3px);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
  }
  
  &:active {
    transform: translateY(-1px);
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.3);
  }
`;

const overlayVariants = {
  hidden: {
    opacity: 0,
  },
  visible: {
    opacity: 1,
    transition: {
      duration: 0.3,
      ease: "easeInOut",
      when: "beforeChildren"
    }
  },
  exit: {
    opacity: 0,
    transition: {
      duration: 0.3,
      ease: "easeInOut",
      when: "afterChildren"
    }
  }
};

const contentVariants = {
  hidden: {
    opacity: 0,
    y: 20
  },
  visible: {
    opacity: 1,
    y: 0,
    transition: {
      duration: 0.5,
      when: "beforeChildren",
      staggerChildren: 0.1
    }
  },
  exit: {
    opacity: 0,
    y: -20,
    transition: {
      duration: 0.3
    }
  }
};

const itemVariants = {
  hidden: { 
    opacity: 0,
    y: 20 
  },
  visible: { 
    opacity: 1,
    y: 0,
    transition: {
      duration: 0.6,
      ease: [0.25, 0.1, 0.25, 1]
    }
  },
  exit: {
    opacity: 0,
    y: -10,
    transition: {
      duration: 0.3
    }
  }
};

const linkVariants = {
  hidden: {
    y: "100%",
    rotateX: -15
  },
  visible: {
    y: 0,
    rotateX: 0,
    transition: {
      duration: 0.6,
      ease: [0.6, 0.05, -0.01, 0.9]
    }
  },
  exit: {
    y: "-100%",
    rotateX: 15,
    transition: {
      duration: 0.3
    }
  }
};

const socialVariants = {
  hidden: {
    opacity: 0,
    y: 20
  },
  visible: {
    opacity: 1,
    y: 0,
    transition: {
      delay: 0.6,
      staggerChildren: 0.1,
      delayChildren: 0.7
    }
  },
  exit: {
    opacity: 0,
    transition: {
      duration: 0.2
    }
  }
};

const socialItemVariants = {
  hidden: {
    opacity: 0,
    y: 20,
    scale: 0.8
  },
  visible: {
    opacity: 1,
    y: 0,
    scale: 1,
    transition: {
      duration: 0.5,
      ease: "easeOut"
    }
  },
  exit: {
    opacity: 0,
    scale: 0.8,
    transition: {
      duration: 0.2
    }
  }
};

const backgroundVariants = {
  hidden: {
    opacity: 0,
    scale: 0.9
  },
  visible: {
    opacity: 1,
    scale: 1,
    transition: {
      duration: 1.5
    }
  },
  exit: {
    opacity: 0,
    scale: 1.1,
    transition: {
      duration: 0.5
    }
  }
};

const MobileMenu = ({ isOpen, toggleMenu }) => {
  const location = useLocation();
  
  // Более агрессивная блокировка скролла при открытом меню
  useEffect(() => {
    if (isOpen) {
      // Сохраняем текущую позицию скролла
      const scrollY = window.scrollY;
      
      // Блокируем скролл основного контента добавляя фиксированную позицию и отрицательный margin
      document.body.style.position = 'fixed';
      document.body.style.width = '100%';
      document.body.style.top = `-${scrollY}px`;
      document.body.style.overflow = 'hidden';
      
      return () => {
        // Восстанавливаем прокрутку
        document.body.style.position = '';
        document.body.style.width = '';
        document.body.style.top = '';
        document.body.style.overflow = '';
        window.scrollTo(0, scrollY);
      };
    }
  }, [isOpen]);
  
  // Закрыть меню по нажатию на Escape
  useEffect(() => {
    const handleEsc = (event) => {
      if (event.key === 'Escape' && isOpen) {
        toggleMenu();
      }
    };
    
    window.addEventListener('keydown', handleEsc);
    
    return () => {
      window.removeEventListener('keydown', handleEsc);
    };
  }, [isOpen, toggleMenu]);
  
  return (
    <MobileMenuContainer>
      <MenuButton onClick={toggleMenu} aria-label="Меню">
        <MenuLine $isOpen={isOpen} />
        <MenuLine $isOpen={isOpen} />
        <MenuLine $isOpen={isOpen} />
      </MenuButton>
      
      <AnimatePresence>
        {isOpen && (
          <MenuOverlay
            variants={overlayVariants}
            initial="hidden"
            animate="visible"
            exit="exit"
          >
            <Background
              variants={backgroundVariants}
              initial="hidden"
              animate="visible"
              exit="exit"
            >
              <BackgroundPattern />
            </Background>
            
            <MenuContent
              variants={contentVariants}
              initial="hidden"
              animate="visible"
              exit="exit"
            >
              <MenuList>
                <MenuItem variants={itemVariants}>
                  <motion.div variants={linkVariants}>
                    <MenuLink 
                      to="/" 
                      className={location.pathname === '/' ? 'active' : ''}
                      onClick={toggleMenu}
                    >
                      Главная
                    </MenuLink>
                  </motion.div>
                </MenuItem>
                <MenuItem variants={itemVariants}>
                  <motion.div variants={linkVariants}>
                    <MenuLink 
                      to="/about" 
                      className={location.pathname === '/about' ? 'active' : ''}
                      onClick={toggleMenu}
                    >
                      Обо мне
                    </MenuLink>
                  </motion.div>
                </MenuItem>
                <MenuItem variants={itemVariants}>
                  <motion.div variants={linkVariants}>
                    <MenuLink 
                      to="/projects" 
                      className={location.pathname === '/projects' ? 'active' : ''}
                      onClick={toggleMenu}
                    >
                      Проекты
                    </MenuLink>
                  </motion.div>
                </MenuItem>
                <MenuItem variants={itemVariants}>
                  <motion.div variants={linkVariants}>
                    <MenuLink 
                      to="/testimonials" 
                      className={location.pathname === '/testimonials' ? 'active' : ''}
                      onClick={toggleMenu}
                    >
                      Отзывы
                    </MenuLink>
                  </motion.div>
                </MenuItem>
                <MenuItem variants={itemVariants}>
                  <motion.div variants={linkVariants}>
                    <MenuLink 
                      to="/contact" 
                      className={location.pathname === '/contact' ? 'active' : ''}
                      onClick={toggleMenu}
                    >
                      Контакты
                    </MenuLink>
                  </motion.div>
                </MenuItem>
              </MenuList>
              
              <SocialLinks
                variants={socialVariants}
                initial="hidden"
                animate="visible"
                exit="exit"
              >
                <SocialLink href="#" target="_blank" rel="noopener noreferrer" variants={socialItemVariants}>
                  <span>FB</span>
                </SocialLink>
                <SocialLink href="#" target="_blank" rel="noopener noreferrer" variants={socialItemVariants}>
                  <span>IG</span>
                </SocialLink>
                <SocialLink href="#" target="_blank" rel="noopener noreferrer" variants={socialItemVariants}>
                  <span>TG</span>
                </SocialLink>
              </SocialLinks>
              
              <MenuFooter
                initial={{ opacity: 0 }}
                animate={{ opacity: 1, transition: { delay: 0.8 } }}
                exit={{ opacity: 0, transition: { duration: 0.2 } }}
              >
                © Портфолио 2023
              </MenuFooter>
            </MenuContent>
          </MenuOverlay>
        )}
      </AnimatePresence>
    </MobileMenuContainer>
  );
};

export default MobileMenu;
