import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import { motion, useAnimation } from 'framer-motion';
import MobileMenu from './MobileMenu';
import logo from '../assets/images/logo.svg';

// Создаем HeaderContainer с корректной обработкой скролла
const HeaderContainer = styled(motion.header)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  padding: ${props => props.$isScrolled ? '1rem 2rem' : '1.5rem 2rem'};
  display: flex;
  justify-content: space-between;
  align-items: center;
  z-index: 100;
  transition: all 0.3s ease-in-out;
  background-color: ${props => 
    props.$isScrolled 
      ? 'rgba(10, 25, 47, 0.85)' 
      : 'transparent'
  };
  backdrop-filter: ${props => 
    props.$isScrolled ? 'blur(10px)' : 'none'
  };
  box-shadow: ${props => 
    props.$isScrolled ? '0 10px 30px -10px rgba(2, 12, 27, 0.7)' : 'none'
  };
`;

// Создаем компоненты с использованием motion для анимаций
const LogoContainer = styled(motion.div)`
  display: flex;
  align-items: center;
`;

const Logo = styled(motion.img)`
  height: 40px;
  cursor: pointer;
`;

const NavContainer = styled.nav`
  display: flex;
  align-items: center;

  @media (max-width: 768px) {
    display: none;
  }
`;

const NavList = styled.ul`
  display: flex;
  list-style: none;
  margin: 0;
  padding: 0;
`;

const NavItem = styled(motion.li)`
  margin: 0 1rem;
`;

const NavLink = styled(Link)`
  color: var(--text-primary);
  text-decoration: none;
  font-size: 1rem;
  font-weight: 500;
  position: relative;
  padding: 0.5rem;

  &:after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 0;
    height: 2px;
    background-color: var(--accent);
    transition: width 0.3s ease;
  }

  &:hover, &.active {
    color: var(--accent);

    &:after {
      width: 100%;
    }
  }
`;

const ActionButton = styled(motion.a)`
  background-color: transparent;
  color: var(--accent);
  border: 1px solid var(--accent);
  border-radius: 4px;
  padding: 0.75rem 1rem;
  font-size: 0.875rem;
  margin-left: 1rem;
  cursor: pointer;
  transition: all 0.3s ease;
  text-decoration: none;

  &:hover {
    background-color: rgba(100, 255, 218, 0.1);
    transform: translateY(-3px);
  }

  &:active {
    transform: translateY(1px);
  }
`;

// Анимационные варианты с корректной кривой Безье
const logoVariants = {
  hidden: { 
    opacity: 0, 
    y: -20,
    transition: {
      duration: 0.5,
      ease: [0.6, 0.05, 0, 0.9]
    }
  },
  visible: { 
    opacity: 1, 
    y: 0,
    transition: {
      duration: 0.5,
      ease: [0.6, 0.05, 0, 0.9]
    }
  }
};

const navItemVariants = {
  hidden: { 
    opacity: 0, 
    y: -20,
    transition: {
      duration: 0.3,
      ease: [0.6, 0.05, 0, 0.9]
    }
  },
  visible: index => ({ 
    opacity: 1, 
    y: 0,
    transition: {
      duration: 0.5,
      delay: index * 0.1,
      ease: [0.6, 0.05, 0, 0.9]
    }
  })
};

const buttonVariants = {
  hidden: { 
    opacity: 0, 
    y: -20,
    transition: {
      duration: 0.3,
      ease: [0.6, 0.05, 0, 0.9]
    }
  },
  visible: { 
    opacity: 1, 
    y: 0,
    transition: {
      duration: 0.5,
      delay: 0.4,
      ease: [0.6, 0.05, 0, 0.9]
    }
  },
  hover: {
    y: -3,
    backgroundColor: "rgba(100, 255, 218, 0.1)",
    transition: {
      duration: 0.2,
      ease: "easeInOut"
    }
  },
  tap: {
    y: 1,
    transition: {
      duration: 0.1,
      ease: "easeInOut"
    }
  }
};

const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const controls = useAnimation();
  const location = useLocation();

  // Обрабатываем событие скролла страницы
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setIsScrolled(true);
      } else {
        setIsScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    // Запускаем анимацию при монтировании
    controls.start('visible');

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [controls]);

  // Переключаем меню
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  // Список пунктов меню
  const navItems = [
    { name: 'Главная', path: '/' },
    { name: 'Обо мне', path: '/about' },
    { name: 'Проекты', path: '/projects' },
    { name: 'Отзывы', path: '/testimonials' },
    { name: 'Контакты', path: '/contact' }
  ];

  return (
    <HeaderContainer 
      $isScrolled={isScrolled}
      $isMenuOpen={isMenuOpen}
      initial="hidden"
      animate="visible"
    >
      <LogoContainer
        variants={logoVariants}
        initial="hidden"
        animate="visible"
      >
        <Link to="/">
          <Logo 
            src={logo} 
            alt="Логотип" 
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          />
        </Link>
      </LogoContainer>

      <NavContainer>
        <NavList>
          {navItems.map((item, index) => (
            <NavItem 
              key={index}
              custom={index}
              variants={navItemVariants}
              initial="hidden"
              animate="visible"
            >
              <NavLink 
                to={item.path} 
                className={location.pathname === item.path ? 'active' : ''}
              >
                {item.name}
              </NavLink>
            </NavItem>
          ))}
        </NavList>

        <ActionButton 
          href="/files/resume.pdf" 
          target="_blank"
          rel="noopener noreferrer"
          variants={buttonVariants}
          initial="hidden"
          animate="visible"
          whileHover="hover"
          whileTap="tap"
        >
          Резюме
        </ActionButton>
      </NavContainer>

      <MobileMenu isOpen={isMenuOpen} toggleMenu={toggleMenu} />
    </HeaderContainer>
  );
};

export default Header;
